function timKiemSP() {
  // ds_sp = SP;
  ds_sp = JSON.parse(localStorage.getItem("product"));
  let tenSP = document.querySelector("#ten_SP").value;
  let loaiSP = document.querySelector("#phan_loai").value;
  let gia1 = document.querySelector("#gia1").value;
  let gia2 = document.querySelector("#gia2").value;
  let thuonghieu = document.querySelector("#thuong_hieu").value;
  if (gia1 >= gia2 && gia1 != 0) {
    alert("Bạn đã chọn sai giá hãy nhập lại?");
    return;
  }

  if (tenSP !== "") {
    ds_sp = ds_sp.filter((value) => {
      return value.ten.toLowerCase().includes(tenSP.toLowerCase());
    });
  }
  if (loaiSP !== "Chọn") {
    ds_sp = ds_sp.filter((value) => {
      return value.loai.toLowerCase().includes(loaiSP.toLowerCase());
    });
    let chiTietLoaiSP = "";
    let ct1 = document.getElementById("ct_loai1");
    let ct2 = document.getElementById("ct_loai2");
    let ct3 = document.getElementById("ct_loai3");
    if (ct1.checked) chiTietLoaiSP = ct1.value;
    else if (ct2.checked) chiTietLoaiSP = ct2.value;
    else if (ct3.checked) chiTietLoaiSP = ct3.value;

    //Nếu 3 thằng trên ko check thì search tất cả theo loại
    //Nếu 3 thằng trên có 1 thằng check thì lọc theo chi tiết loại
    if (chiTietLoaiSP !== "") {
      ds_sp = ds_sp.filter((value) => {
        return value.chiTietLoai
          .toLowerCase()
          .includes(chiTietLoaiSP.toLowerCase());
      });
    }
  }

  if (gia1 < gia2) {
    ds_sp = ds_sp.filter((value) => {
      return value.gia >= gia1 && value.gia <= gia2;
    });
  }

  if (thuonghieu !== "Chọn") {
    ds_sp = ds_sp.filter((value) => {
      return value.thuonghieu.toLowerCase().includes(thuonghieu.toLowerCase());
    });
  }
  console.log(tenSP + " " + loaiSP + " " + thuonghieu);
  console.log(ds_sp);
  //Tìm xong sẽ xuất danh sách pử trang đầu
  xuatDSSP();
}

