function cap_nhat_sp(num) {
  let dssp = JSON.parse(localStorage.getItem("product"));
  let sp = ds_sp[num];
  let index = dssp.findIndex((tmp) => tmp.id === sp.id);
  let ten = document.getElementById("ten_sp_sua");
  let gia = document.getElementById("gia_sp_sua");
  let anh = document.getElementById("anh_form_sua123");
  console.log(anh.src);
  if (ten.value === "") {
    alert("Bạn chưa nhập tên sản phẩm!");
    return;
  }
  if (gia.value === "") {
    alert("Bạn chưa nhập giá sản phẩm!");
    return;
  }

  let checkXoaAnh = document.getElementById("boAnhHienTai");

  //Cật nhật giá trị của tên và giá
  sp.ten = ten.value;
  sp.gia = gia.value;
  if (
    anh.src !== "" &&
    (checkXoaAnh.checked ||
      anh.src !==
        "http://127.0.0.1:5500/img_admin/img_product/giay/default_shoes.png")
  ) {
    let chonAnh = document.getElementById("chonAnhSua");
    if (chonAnh.value === "1") sp.anh1 = anh.src;
    else if (chonAnh.value === "2") sp.anh2 = anh.src;
    else if (chonAnh.value === "3") sp.anh3 = anh.src;
    else if (chonAnh.value === "4") sp.anh4 = anh.src;
    else if (chonAnh.value === "5") sp.anh5 = anh.src;
  }
  //Đưa ds mới cập nhật lên localStorage
  dssp[index] = sp;
  localStorage.setItem("product", JSON.stringify(dssp));
  // console.log(anh.files);
  dong_form_sua();
  let k = num;
  if (k % perPage == 0) k += perPage / 10;
  renderPage1();
  handlePage1(Math.ceil(k / perPage));
}

