function sapXepDH() {
  for (let i = 0; i < ds_dh.length - 1; i++) {
    for (let j = i + 1; j < ds_dh.length; j++) {
      let dc1 = ds_dc.find((dc) => dc.maKH === ds_dh[i].maKH).quan_huyen;
      let dc2 = ds_dc.find((dc) => dc.maKH === ds_dh[j].maKH).quan_huyen;
      if (dc1 > dc2) {
        let tmp = ds_dh[i];
        ds_dh[i] = ds_dh[j];
        ds_dh[j] = tmp;
      }
    }
  }
  xuatDS_DH();
}

